// --- CORE FUNCTIONS (CÁC HÀM XỬ LÝ CHÍNH) ---

/**
 * Renders the main content sections (Weeks) and populates the navigation.
 */
function renderContent() {
    const container = document.getElementById('content-container');
    const nav = document.getElementById('nav-links');
    const mobileNav = document.getElementById('mobile-nav-links');

    // Xóa nội dung cũ trong container để tránh lặp lại
    container.innerHTML = '';
    nav.innerHTML = '';
    mobileNav.innerHTML = '';


    reportContent.forEach(item => {
        // 1. Create the content block (Accordion)
        const section = document.createElement('div');
        section.id = item.id;
        // Cập nhật màu nền và viền cho Light Mode
        section.className = 'bg-white p-4 sm:p-6 rounded-xl shadow-lg border border-slate-200 hover:border-slate-800 transition duration-300';
        
        section.innerHTML = `
            <div class="accordion-header flex justify-between items-center cursor-pointer p-2 sm:p-4 rounded-lg bg-slate-100" onclick="toggleAccordion('${item.id}-body')">
                <h2 class="text-xl sm:text-2xl font-bold text-slate-800 tracking-wide">${item.title}</h2>
                <svg id="${item.id}-icon" class="w-6 h-6 transform transition-transform duration-300 text-slate-800" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M19 9l-7 7-7-7"></path></svg>
            </div>
            <div id="${item.id}-body" class="accordion-body max-h-0 overflow-hidden transition-all duration-500 ease-in-out">
                <div class="p-4 pt-6 text-slate-700 space-y-4">
                    ${item.content}
                </div>
            </div>
        `;
        container.appendChild(section);

        // 2. Create the desktop navigation link
        const navLink = document.createElement('a');
        navLink.href = `#${item.id}`;
        navLink.className = 'sidebar-link block py-2 px-4 rounded-lg text-slate-700 transition duration-150 ease-in-out text-sm';
        navLink.textContent = item.title;
        navLink.onclick = (e) => {
            e.preventDefault();
            scrollToSection(item.id);
        };
        nav.appendChild(navLink);

        // 3. Create the mobile navigation link
        const mobileNavLink = navLink.cloneNode(true);
        mobileNavLink.onclick = (e) => {
            e.preventDefault();
            scrollToSection(item.id);
            document.getElementById('mobile-sidebar').classList.remove('translate-x-0'); // Close menu after click
        };
        mobileNav.appendChild(mobileNavLink);
    });

    // Add Group Members link to navigation
    const groupLinkText = 'Members of my group';
    ['nav-links', 'mobile-nav-links'].forEach(id => {
        const groupLink = document.createElement('a');
        groupLink.href = `#group-members`;
        groupLink.className = 'sidebar-link block py-2 px-4 rounded-lg text-slate-700 transition duration-150 ease-in-out font-bold mt-4 text-sm';
        groupLink.textContent = groupLinkText;
        groupLink.onclick = (e) => {
            e.preventDefault();
            scrollToSection('group-members');
            if (id === 'mobile-nav-links') {
                document.getElementById('mobile-sidebar').classList.remove('translate-x-0');
            }
        };
        document.getElementById(id).appendChild(groupLink);
    });
}

/**
 * Renders the creative group member progress cards with updated MSSV and format.
 */
function renderGroupMembers() {
    const container = document.getElementById('member-cards');
    // Xóa nội dung cũ để cập nhật
    container.innerHTML = ''; 

    groupMembers.forEach((member, index) => {
        const card = document.createElement('div');
        // Cập nhật màu nền cho Light Mode
        card.className = 'bg-white p-6 rounded-xl shadow-lg transition duration-300 hover:scale-[1.02] border border-slate-300';
        card.innerHTML = `
            <div class="flex items-center mb-4">
                <div class="w-12 h-12 bg-slate-800 rounded-full flex items-center justify-center text-white font-extrabold text-xl shadow-md">${index + 1}</div>
                <div class="ml-4">
                    <h4 class="text-xl font-bold text-slate-800">${member.name}</h4>
                    <p class="text-sm text-slate-500">MSSV: ${member.mssv}</p>
                </div>
            </div>
            
            <div class="mt-4">
                <div class="flex justify-between text-sm font-medium text-slate-700 mb-1">
                    <span>Project Contribution</span>
                    <span class="text-slate-800">${member.progress}%</span>
                </div>
                <div class="w-full bg-slate-300 rounded-full h-3">
                    <div class="progress-bar-fill h-3 rounded-full bg-slate-800 shadow-md shadow-slate-800/30" style="width: ${member.progress}%"></div>
                </div>
            </div>
        `;
        container.appendChild(card);
    });
}

/**
 * Toggles the visibility of an accordion body.
 * @param {string} id The ID of the accordion body element.
 */
window.toggleAccordion = function(id) {
    const body = document.getElementById(id);
    const iconId = id.replace('-body', '-icon');
    const icon = document.getElementById(iconId);

    if (body.classList.contains('max-h-0')) {
        // Open the accordion
        body.classList.remove('max-h-0');
        body.style.maxHeight = body.scrollHeight + "px";
        icon.classList.add('rotate-180');
    } else {
        // Close the accordion
        body.style.maxHeight = '0';
        body.classList.add('max-h-0');
        icon.classList.remove('rotate-180');
    }
}

/**
 * Scrolls smoothly to a section and updates the active navigation link.
 * @param {string} id The ID of the target section.
 */
function scrollToSection(id) {
    const target = document.getElementById(id);
    if (target) {
        window.scrollTo({
            top: target.offsetTop - 30, // Offset for padding
            behavior: 'smooth'
        });
    }
}

/**
 * Handles scroll events to highlight the current section in the sidebar.
 */
function handleScroll() {
    // Include 'group-members' in the list of sections
    const sections = reportContent.map(item => document.getElementById(item.id));
    sections.push(document.getElementById('group-members'));

    let current = '';

    sections.forEach(section => {
        // Use a margin of 100px from the top of the viewport
        if (section && window.scrollY >= section.offsetTop - 100) {
            current = section.id;
        }
    });

    // Update navigation links for both desktop and mobile
    document.querySelectorAll('.sidebar-link').forEach(link => {
        link.classList.remove('active');
        if (link.getAttribute('href').includes(current)) {
            link.classList.add('active');
        }
    });
}

// --- EVENT LISTENERS AND INITIALIZATION ---

window.onload = function() {
    renderContent();
    renderGroupMembers();
    
    // Add initial active class to the first link
    const firstLink = document.querySelector('#nav-links a');
    if (firstLink) {
        firstLink.classList.add('active');
    }

    // Mobile menu toggles
    document.getElementById('menu-toggle').addEventListener('click', () => {
        document.getElementById('mobile-sidebar').classList.add('translate-x-0');
    });

    document.getElementById('close-menu').addEventListener('click', () => {
        document.getElementById('mobile-sidebar').classList.remove('translate-x-0');
    });

    // Scroll listener for highlighting active link
    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Initial check
};